package workspace;

import java.util.*;

public class AB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int dan;
		int i = 0;
		Scanner sc = new Scanner(System.in);
		dan = sc.nextInt();
		while (i < 9) {
			i++;
			System.out.println(dan + " * " + i + " = " + dan * i);
		}

	}

}
